import React, { useState, Fragment } from "react";
import "./FormInput.css";

const FormInput = () => {
  const [enteredName, setenteredName] = useState("");
  const [enteredage, setenteredage] = useState("");
  // const [enteredgender, setenteredgender] = useState("");
  const [enteredEmail, setenteredEmail] = useState("");
  const [enteredPhone, setenteredPhone] = useState("");
  const registerHandler = (event) => {
    event.preventDefault();
    console.log(enteredName, enteredEmail, enteredage, enteredPhone);
  };
  const namechangeHandler = (event) => {
    setenteredName(event.input.value);
  };
  const agechangeHandler = (event) => {
    setenteredage(event.input.value);
  };
  const emailchangeHandler = (event) => {
    setenteredEmail(event.input.value);
  };
  const phonechangeHandler = (event) => {
    setenteredPhone(event.input.value);
  };
  return (
    <Fragment>
      <div className="form-container">
        <form onSubmit={registerHandler}>
          <div>
            <label>Name:</label>
            <input type="text" onChange={namechangeHandler} />
          </div>
          <div>
            <label>Age:</label>
            <input type="text" onChange={agechangeHandler}></input>
          </div>
          <div>
            <label htmlFor="gender">Gender:</label>
            <input type="radio" name="gender" value="male" />
            Male
            <input type="radio" name="gender" value="female" />
            Female
            <input type="radio" name="gender" value="other" />
            Other
          </div>
          <div>
            <label>Email:</label>
            <input type="email" onChange={emailchangeHandler}></input>
          </div>
          <div>
            <label>Phone Number:</label>
            <input type="text" onChange={phonechangeHandler} />
          </div>
          <div>
            <button type="submit">Register</button>
          </div>
        </form>
      </div>
    </Fragment>
  );
};

export default FormInput;
